const slideVariants = {
    enter: (direction) => ({
        x: direction > 0 ? '100%' : '-100%',
        opacity: 0
    }),
    center: {
        x: 0,
        opacity: 1
    },
    exit: (direction) => ({
        x: direction < 0 ? '100%' : '-100%',
        opacity: 0
    })
};

const transition = {
    type: "tween",
    ease: "easeInOut",
    duration: 0.3
};

export default function TrainContent({ view, level, base }) {
    const [[page, direction], setPage] = useState([0, 0]);
    const [selectedWeek, setSelectedWeek] = useState(null);
    const [selectedTraining, setSelectedTraining] = useState(null);
    const [activeIndex, setActiveIndex] = useState(0);
    const [direct, setDirect] = useState(0);

    const handleWeekSelect = (weekData) => {
        setSelectedWeek(weekData);
        setPage([1, 1]);
    };

    const handleTrainingSelect = (training) => {
        setSelectedTraining(training);
        // Перед переходом на страницу упражнений, очищаем handleBack
        window.handleBack = null;
        document.body.removeAttribute('data-handle-back');
        setPage([2, 1]);
    };

    const handleBack = useCallback(() => {
        if (page === 2) {
            setSelectedTraining(null);
            setPage([1, -1]);
        } else if (page === 1) {
            setSelectedWeek(null);
            setPage([0, -1]);
        } else if (page === 0) {
            window.showContent = false;
            window.dispatchEvent(new Event('showContentChange'));
        }
    }, [page]);

    useEffect(() => {
        // Устанавливаем handleBack только если мы не на странице упражнений
        if (page !== 2) {
        window.handleBack = handleBack;
            document.body.setAttribute('data-handle-back', 'true');
        } else {
            window.handleBack = null;
            document.body.removeAttribute('data-handle-back');
        }

        return () => {
            window.handleBack = null;
            document.body.removeAttribute('data-handle-back');
        };
    }, [page]); // Убираем handleBack из зависимостей

    // Если view === warmup, показываем разминку
    if (view === 'warmup') {

        const handleComplete = () => {
            window.handleBack = null;
            window.showContent = false;
            window.dispatchEvent(new Event('showContentChange'));
        };

        const handleSelect = (index) => {
            setDirect(index > activeIndex ? 1 : -1);
            setActiveIndex(index);
        };

        return (
            <div className="warmupContent">
                {!base && <Selecter
                    textOne='Разминка'
                    textTwo='МФР'
                    activeIndex={activeIndex}
                    onClick={handleSelect}
                />}
                <AnimatePresence initial={false} custom={direct} mode="wait">
                    <motion.div
                        key={activeIndex}
                        className="warmup-screen"
                        custom={direct}
                        variants={slideVariants}
                        initial="enter"
                        animate="center"
                        exit="exit"
                        transition={transition}
                    >
                        {activeIndex === 0 ? (
                            <div className="warmup-content">
                                        <div className="warmup-videos">
                                    {warmup.Z.map((item, index) => (
                                        <div key={index} className="videoContent">
                                            <div className="video-section">
                                                        <div className="videoContainer">
                                                            <iframe
                                                        src={item.videoUrl}
                                                                allow="autoplay; fullscreen; picture-in-picture; encrypted-media; gyroscope; accelerometer; clipboard-write; screen-wake-lock;"
                                                                frameBorder="0"
                                                                allowFullScreen
                                                                title={`Разминка ${index + 1}`}
                                                            />
                                                        </div>
                                                    </div>
                                            <p style={{ color: '#0D0D0D', fontSize: '16px' }}>{item.text}</p>
                                                </div>
                                            ))}
                                        </div>
                                        <div className="warmup-navigation">
                                            <Button
                                                onClick={handleComplete}
                                                text="Завершить разминку"
                                                icon={check}
                                                bg="#CBFF52"
                                                bgFocus="#EBFFBD"
                                                color="#0D0D0D"
                                                width="100%"
                                            />
                                        </div>
                            </div>
                        ) : (
                            <div className="warmup-content">
                                {warmup.M.map((item, index) => (
                                    <div key={index} className="video-section">
                                        <div className="videoContainer">
                                            <iframe
                                                src={item.videoUrl}
                                                allow="autoplay; fullscreen; picture-in-picture; encrypted-media; gyroscope; accelerometer; clipboard-write; screen-wake-lock;"
                                                frameBorder="0"
                                                allowFullScreen
                                                title={`МФР ${index + 1}`}
                                            />
                                        </div>
                                        <p>{item.text}</p>
                                    </div>
                                ))}
                                <div className="warmup-navigation">
                                            <Button
                                                onClick={handleComplete}
                                                text="Завершить МФР"
                                                icon={check}
                                                bg="#CBFF52"
                                                bgFocus="#EBFFBD"
                                                color="#0D0D0D"
                                                width="100%"
                                            />
                                        </div>
                            </div>
                        )}
                    </motion.div>
                </AnimatePresence>
            </div>
        );
    }

    // Если view === train, показываем PdfViewer
    if (view === 'train') {
        return (
            <div className="trainContent">
                <PdfViewer pdfFile={pdf} />
            </div>
        );
    }

    // Определяем, какие данные использовать в зависимости от view
    const trainingData = view === 'gym' ? gym : view === 'home' ? home : null;

    return (
        <div className="trainContent">
            <AnimatePresence initial={false} custom={direction}>
                {page === 0 && (
                    <motion.div
                        key="weeks"
                        className="weeks-screen"
                        custom={direction}
                        variants={slideVariants}
                        initial="enter"
                        animate="center"
                        exit="exit"
                        transition={transition}
                    >
                        {trainingData?.map((item, index) => (
                            <TrainBox
                                key={index}
                                data={item}
                                train_count={item.trainings.length}
                                onClick={() => handleWeekSelect(item)}
                            />
                        ))}
                    </motion.div>
                )}

                {page === 1 && selectedWeek && (
                    <motion.div
                        key="trainings"
                        className="trainings-screen"
                        custom={direction}
                        variants={slideVariants}
                        initial="enter"
                        animate="center"
                        exit="exit"
                        transition={transition}
                    >
                        <div className="trainings-grid">
                            {selectedWeek.trainings.map((training, index) => (
                                <TrainBox
                                    key={index}
                                    data={training}
                                    onClick={() => handleTrainingSelect(training)}
                                    level={level}
                                />
                            ))}
                        </div>
                    </motion.div>
                )}

                {page === 2 && selectedTraining && (
                    <motion.div
                        key="training-details"
                        className="screen training-details-screen"
                        custom={direction}
                        variants={slideVariants}
                        initial="enter"
                        animate="center"
                        exit="exit"
                        transition={transition}
                    >
                        <TrainingPage
                            trainingData={selectedTraining}
                            level={level}
                            onBack={handleBack}
                        />
                    </motion.div>
                )}
            </AnimatePresence>
        </div>
    );
} 